mapper.select:
		SELECT COALESCE(sum(consumed_point), 0)
        from tbl_point_history
        where EXTRACT(MONTH FROM start_date) = #{month}
        and category_code = '01'
        and member_code = #{memberCode}

		SELECT COALESCE(sum(consumed_point), 0)
		from tbl_point_history
		where 

		start_date <= #{currentDate}

        and category_code = '02'
		and member_code = #{memberCode}

select * from ( select * , cast(start_date as date) as registerDateFormatted from tbl_point_history where member_code = #{memberCode}
and category_code = #{categoryCode}
)p inner join (select cd ,name AS pointTypeName from code_master where code_type='007' ) cdm on p.point_code = cdm.cd
