mapper.select:
select * from ( select * , cast(start_date as date) as startDateFormatted , cast(register_date as date) as registerDateFormatted from tbl_point_manage
and member_code = #{memberCode}
and register_user = #{registerUserCode}
and EXTRACT(YEAR FROM register_date) || '-' || EXTRACT(MONTH FROM register_date) = #{registeredMonth}
and EXTRACT(YEAR FROM start_date) || '-' || EXTRACT(MONTH FROM start_date) = #{startMonth}
)p inner join (select member_code as registerUserCode ,last_name || ' ' ||first_name as registerUserName from tbl_user where member_type_cd = '01'
and CONCAT(last_name,first_name) like CONCAT('%',REPLACE(#{registerUserName},'%','¥%'),'%') ESCAPE '¥' or CONCAT(last_name_kana,first_name_kana) like CONCAT('%',REPLACE(#{registerUserName},'%','¥%'),'%') ESCAPE '¥'
)m on p.register_user = m.registerUserCode inner join (select * from tbl_user
and asp_code = #{aspCode}
and CONCAT(last_name,first_name) like CONCAT('%',REPLACE(#{name},'%','¥%'),'%') ESCAPE '¥' or CONCAT(last_name_kana,first_name_kana) like CONCAT('%',REPLACE(#{name},'%','¥%'),'%') ESCAPE '¥'
and member_type_cd != '01'
)u on p.member_code = u.member_code inner join (select cd ,name AS pointTypeName from code_master where code_type='009' ) cdm on p.point_type = cdm.cd
