mapper.select:
select rev.reservation_id as reservation_id , rev.reservation_number as reservation_number , rev.status as status , rev.member_code as member_code , rev.asp_code as asp_code , rev.bat_number as bat_number , rev.reservation_date as reservation_date , rev.reservation_time as reservation_time , rev.consumed_point as consumed_point , rev.point_category_code as point_category_code , rev.grade_type_cd as grade_type_cd , rev.register_user as register_user , rev.register_date as register_date , rev.update_user as update_user , rev.update_date as update_date , asp.asp_name as asp_name , usr.last_name as last_name , usr.first_name as first_name , case when to_timestamp(to_char(rev.reservation_date, 'YYYY/MM/DD')||' '||substring(rev.reservation_time, 0, 3), 'YYYY/MM/DD HH24') > current_timestamp then 0 else 1 end as expire_flag from tbl_reservation rev inner join tbl_asp asp on rev.asp_code = asp.asp_code left outer join tbl_user usr on rev.member_code = usr.member_code
and rev.reservation_number like CONCAT('%',REPLACE(#{reservationNumber},'%','짜%'),'%') ESCAPE '짜'
and rev.member_code like CONCAT('%',REPLACE(#{memberCode},'%','짜%'),'%') ESCAPE '짜'
and rev.bat_number = #{batNumber}
and rev.asp_code = #{aspCode}
and rev.reservation_date = to_date(#{reservationDate}, 'YYYY/MM/DD')
and rev.reservation_time = #{reservationTime}
and rev.status = #{status}
and (rev.status = '1' or (rev.status = '0' and rev.register_date > current_timestamp + '-3 minute' ))
and to_timestamp(to_char(rev.reservation_date, 'YYYY/MM/DD')||' '||substring(rev.reservation_time, 0, 3), 'YYYY/MM/DD HH24') > current_timestamp
order by reservation_date desc, reservation_time desc

select pm.id as point_manage_id , pm.member_code as member_code , pm.point_type as point_type , pm.point_amount as point_amount , pm.reservation_number as reservation_number , pm.consumed_point as consumed_point , to_char(pm.start_date, 'YYYY/MM/DD') as start_date , to_char(pm.end_date, 'YYYY/MM/DD') as end_date , pm.category_code as category_code from tbl_point_manage pm where pm.member_code = #{memberCode}
and pm.category_code = #{categoryCode}
and to_date(#{reservationDate}, 'YYYY/MM/DD') between pm.start_date and pm.end_date
and pm.start_date > (DATE_TRUNC('month', now()) +'-1 Day')
order by pm.end_date, pm.start_date

select v.*
from
    view_reservation_point_time_table v 
where
    v.date_time = #{dateTime}

    and v.validate_start_term <= #{validateStartTerm}
